          this.state.cards.sort(() => Math.random() - 1)
          return true; 
        }
        } else {
          this.gameOver();
        }

    });
  }

  render() {
    return (
    <div>
        <Title score={this.state.score} highScore={this.state.highScore}></Title>
      <Wrapper>
        {this.state.cards.map(cards => (
          <PlayCards 
            countOnClick={this.countOnClick}
            key={cards.id}
            image={cards.image}
            //count={this.count}
          />
        ))}
     </Wrapper>   
  </div>
    );
  }
}

export default App;
